/**
 * 从 Iconfont 的 js 中抽取图标列表
 * @param jsText
 */

export var extractListFormIconfontJS = function extractListFormIconfontJS(jsText) {
  // https://regex101.com/r/ZxzbHb/1
  var regex = /<symbol id="((?:\w|-)*)"/g;
  return Array.from(jsText.matchAll(regex)).map(function (i) {
    return i[1];
  });
};

/**
 * 从 List 中找到临近的有效 index
 * @param list
 * @param currentIndex
 */
export var findNeighborIndex = function findNeighborIndex(list, currentIndex) {
  return currentIndex === 0 ? 0 : list.length <= currentIndex + 1 ? currentIndex - 1 : currentIndex;
};